import React, { useEffect, useState } from 'react';

const Home = () => {
    const [selectedPart, setSelectedPart] = useState('');
    const [searchByNumber, setSearchByNumber] = useState(true)

    const [parts, setParts] = useState({});

    useEffect(() => {
        console.log('bool ', searchByNumber)
    }, [searchByNumber])

    // Event handler for input changes
    const handleInputChange = (event) => {
        setSelectedPart(event.target.value);
    };

    const getPartByDescription = async (description) => {
        try {
            const response = await fetch(`https://localhost:7221/Parts/GetPartByDescription?description=${description}`);
            const data = await response.json();
            console.log('data ', data)

            setParts(data);
        } catch (error) {
            console.error('Error fetching parts:', error);
        }
    };

    const getPartByNumber = async (sequence) => {
        try {
            const response = await fetch(`https://localhost:7221/Parts/GetPartByNumber?sequence=${sequence}`);
            const data = await response.json();
            console.log('data: ', data)

            setParts(data);
        } catch (error) {
            console.error('Error fetching parts:', error);
        }
    }

    return (
        <div>
            <div style={{ display: 'flex', justifyContent: 'center', marginTop: 10 }}>
                <select>
                    <option onClick={() => { setSearchByNumber(true) }}>Search By Item Number</option>
                    <option onClick={() => { setSearchByNumber(false) }}>Search By Item Description</option>
                </select>
                <input
                    type="text"
                    id="itemInfo"
                    value={selectedPart}
                    onChange={handleInputChange}
                />
                <button onClick={() => { searchByNumber ? getPartByNumber(selectedPart) : getPartByDescription(selectedPart) }}>
                    Submit
                </button>
            </div>
        </div>
    );
};

export default Home;
